import {createContext, useState} from "react";
import React from 'react'

export const ErrorTablePageContext = createContext([{}, () =>{}])

export const ErrorTablePageProvider = ({children}) =>{
    const [state, setState] = useState({
        hasError: false,
        errors: null
    })
    return(
        <ErrorTablePageContext.Provider value={[state, setState]}>{children}</ErrorTablePageContext.Provider>
    )
} 