import {useLocation, Navigate} from 'react-router-dom'
import useAuth from '../hooks/useAuth'


const RequireUnAuth = ({children}) => {
    const location = useLocation()
    const auth = useAuth()[0].isLoggedIn

    console.log('location - ', location)
    console.log('auth in RequireUnAuth - ', auth)

    // return <Navigate to={location.pathname} state={{from: location}}/>

    if(auth){         
        if(location.pathname !== '/login' && location.pathname !== '/register'){
            return <Navigate to={location.pathname} state={{from: location}}/>
        }
        return <Navigate to='/global' state={{from: location}}/>
    }

return children;
}

export {RequireUnAuth}