import React from "react";

const BackendErrorMessages = ({backendErrors})=>{
    console.log('backenderror', backendErrors)
    const errormessages = backendErrors.data.errors.map( error =>{
        return  `${error.path}` + ' ' + `${error.message}`
    })
    
   
    // const errorMessages = `${backendErrors.errors.path}` `${backendErrors.errors.message}`
   
    //     return `${backendErrors.path} ${errormessages}`
    // }
    console.log('errormessages', errormessages)
    return (
        <ul className="error-messages">
                {errormessages.map(errorMessage =>{
                    return <li key={errorMessage}>{errorMessage}</li>
                })}
        </ul>
    )}


export default BackendErrorMessages;